function swalprompt(e,t,n,r,i){swal({title:e,text:t,type:"",showCancelButton:true,confirmButtonColor:"#f39c12",cancelButtonText:r,confirmButtonText:n,closeOnConfirm:true},function(){var e=$("#swal-input").val();i(e)});$(".sweet-alert > p").append('<input id="swal-input" class="form-control">');$("#swal-input").focus();$("#swal-input").keydown(function(e){if(e.keyCode==32){$(this).val($(this).val()+" ");return false}})}
